function correctPolys(polys, approx = 10) {
  let clusters = [];
  for (const p of polys) {
    for (const pt of p) {
      let found = false;
      for (const cl of clusters) {
        for (const v of cl) {
          let dx = Math.abs(v.x - pt.x);
          let dy = Math.abs(v.y - pt.y);
          if (dx < approx && dy < approx) {
            cl.push(pt);
            found = true;
            break;
          }
        }
        if (found) break;
      }
      if (!found) {
        clusters.push([pt]);
      }
    }
  }
  let vertices = [];
  for (const cl of clusters) {
    let sumx = 0;
    let sumy = 0;
    let len = cl.length;
    for (const pt of cl) {
      sumx += pt.x;
      sumy += pt.y;
    }
    vertices.push({ x: Math.round(sumx / len), y: Math.round(sumy / len) });
  }
  for (const p of polys) {
    for (const pt of p) {
      let found = false;
      for (const v of vertices) {
        let dx = Math.abs(v.x - pt.x);
        let dy = Math.abs(v.y - pt.y);
        if (dx < approx && dy < approx) {
          if (dx != 0 || dy != 0) {
            pt.x = v.x;
            pt.y = v.y;
          }
          found = true;
        }
        if (found) break;
      }
      if (!found) {
        error('point not found in vertices!!! ' + pt.x + ' ' + pt.y);
      }
    }
  }
  return vertices;
}
