  let rnd3 = rnd2 + randomNumber(1, 5);
  let nums = sameNum ? [num, num, num, num] : [num, num + rnd1, num > 5 ? (num - rnd2) : num + rnd2, num + rnd3];
  let den = fr.d;
  let denoms = sameDenom ? [den, den, den, den] : sameNum ? [den, den + 1, den + 2, den > 2 ? den - 1 : den + 3]
    : [den, den + 1, den + 2, den];
  let frlist = [];
