function jsSafeStringify(obj, indent = 2) {
  let cache = [];
  const retVal = JSON.stringify(
    obj,
    (key, value) =>
      typeof value === "object" && value !== null
        ? cache.includes(value)
          ? undefined 
          : cache.push(value) && value 
        : value,
    indent
  );
  cache = null;
  return retVal;
};
