function ncdf(x, mean = 100, std = 15) {
  var x = (x - mean) / std
  var t = 1 / (1 + .2315419 * Math.abs(x))
  var d = .3989423 * Math.exp(-x * x / 2)
  var prob = d * t * (.3193815 + t * (-.3565638 + t * (1.781478 + t * (-1.821256 + t * 1.330274))))
  if (x > 0) prob = 1 - prob
  return prob
}
function neighborhood(items, byrc) {
  let adjList = [];
  let di = {};
  for (const info of items) {
    if (info.type != 'field') continue;
    let [r, c] = [info.row, info.col];
    info.nodeItems = [
      lookup(byrc, [r - 2, c]),
      lookup(byrc, [r - 1, c + 1]),
      lookup(byrc, [r + 1, c + 1]),
      lookup(byrc, [r + 2, c]),
      lookup(byrc, [r + 1, c - 1]),
      lookup(byrc, [r - 1, c - 1]),
    ];
    info.nodes = info.nodeItems.map(x => isdef(x) ? x.id : null);
    delete info.nodeItems;
    for (let i = 0; i < 6; i++) {
      let n1 = info.nodes[i];
      if (n1 == null) continue;
      let n2 = info.nodes[(i + 1 % 6)];
      if (n2 == null) continue;
      if (lookup(di, [n1, n2]) || lookup(di, [n2, n1])) continue;
      lookupSet(di, [n1, n2], true);
      adjList.push([n1, n2]);
    }
    info.neiItems = [
      lookup(byrc, [r - 3, c + 1]),
      lookup(byrc, [r, c + 2]),
      lookup(byrc, [r + 3, c + 1]),
      lookup(byrc, [r + 3, c - 1]),
      lookup(byrc, [r, c - 2]),
      lookup(byrc, [r - 3, c - 1]),
    ];
    info.nei = info.neiItems.map(x => isdef(x) ? x.id : null);
    delete info.neiItems;
  }
}
