function posToPoint(pos = 'cc', w, h, offx = 0, offy = 0) {
  let di = { t: 0, b: h, l: 0, r: w };
  let py = pos[0] == 'c' ? h / 2 : di[pos[0]];
  let px = pos[1] == 'c' ? w / 2 : di[pos[1]];
  return [px + offx, py + offy];
}
