function toBase10(s, base = 16) {
  let s1 = reverseString(s.toLowerCase());
  let res = 0;
  let mult = 1;
  for (let i = 0; i < s1.length; i++) {
    let l = s1[i];
    let hexarr = ['a', 'b', 'c', 'd', 'e', 'f'];
    let n = isNumber(l) ? Number(l) : 10 + hexarr.indexOf(l);
    res += mult * n;
    mult *= base;
  }
  return res;
}
