function turtle() {
  background(51);
  stroke(255); 
  translate(width/2,height);
  for (let i = 0; i < sentence.length; i++) {
    let x = sentence.charAt(i);
    if ('ABF'.includes(x)) {line(0, 0, 0, -len);translate(0,-len);}
    else if (x == '+') rotate(angle);
    else if (x == '-') rotate(-angle);
    else if (x == '[') push(); 
    else if (x == ']') pop();
  }
}
